import styled from 'styled-components';

export const Title = styled.div`

    display: flex;
    align-items: center;
    justify-content:space-around;
    padding:15px;
    color: white;
    font-size:25px;
    background-color:lightpink;
`
export const LogoImage = styled.img`
 
 width: 5vw;
 height:5vh;
 /* border-radius:50%; */

`
export const AnchorTag = styled.a`
 
cursor:pointer;

&:hover {
    color:black;
  }
`
