import React from "react";
import {
  Input,
  SearchSpace,
  SortSpace,
  FilterSpace,
  FilterDivison,
  CardContainer,
  CardItem
} from "./styles";
import CardItemDetails from "./CardItemDetails";
import CardDisplay from "./CardDisplay";

export const CardItems: React.FC = () => {
  return (
    
    <CardItem>
    {CardItemDetails.map((product) => {
      return (
        <CardDisplay
          id={product.productId}
          name={product.productName}
          brand={product.productBrand}
          price={product.productPrice}
          rating={product.productRating}
          image={product.productImage}
          
        />
      );
    })}
 </CardItem>
    // <CardContainer>
    //   {
    //   CardItemDetails.map((product: any)=> (
    //     <>
    //       <h4>{product.productName}</h4>
    //       <div>Brand:{product.productBrand}</div>
    //       <div>Price:{product.productPrice}</div>
    //       <div>Ratings:{product.productRating}</div>
    //       {/* <img src={product.productImage}></img> */}
    //     </>
    //   ))}
    // </CardContainer>
  );

  // const products=[
  //     {
  //         name:"Loga",
  //         age:21,
  //         designation:"student",
  //     },
  //     {
  //         name:"Jaan",
  //         age:20,
  //         designation:"student",
  //     },
  //     {
  //         name:"Maals",
  //         age:20,
  //         designation:"student",
  //     },
  //     {
  //         name:"Priya",
  //         age:23,
  //         designation:"student",
  //     },

  //]

  const [productList,setProductList]=React.useState<{productId:number,
    productName:string,
    isFeatured:boolean,
    productBrand:string,
    productRating:number,
    productImage?: String,
    productPrice:Number}[] | undefined>(CardItemDetails);
  const [text,setText]=React.useState<string>('');
  const handleOnClick=()=>
  {
    const findproducts=
    productList && productList?.length>0 ? productList?.filter((u)=>u?.productName===text)
    :undefined;
    setProductList(findproducts);
  };
      return (
       <div>

          {/* <Input type="text" placeholder="Search" ></Input> */}
          <input type="text"
          placeholder="Search Product"
          value={text}
          onChange={(e)=>{setText(e.target.value);
           setProductList(CardItemDetails);
          }}></input>
          <button disabled={!text} onClick={handleOnClick}>SEARCH</button>
          {/* </SearchSpace> */}
          <div>
              {
                  productList && productList?.length===0 && (
                      <div>Oops, No Product Found :(</div>
                  )
              }
              {

                  productList && productList?.length>0 && productList?.map(product =>{
                      return(
                     <div>
                      <h3>Name:{product?.name}</h3>
                      <p>Age:{product?.age}</p>
                      <p>Designation:{product?.designation}</p>
                     </div>
                      );
                  })
              }
          </div>
       </div>

      );
};
