import {featuredContent} from "../../data/String";
import CardItemDetails from "./CardItemDetails";
import { DisplayFeatured } from "./DisplayFeatured";
import SearchDisplay from "./SearchDisplay";
import { Heading,FeaturedBox} from "./styles";

export const FeaturedItems:React.FC=()=> {


    return (
     <FeaturedBox>
        <Heading>
      {featuredContent.heading}
      </Heading>
      <DisplayFeatured>
        {
          CardItemDetails.map((product)=>
          {
            return product.isFeatured && (
              <Sear
              id={product.productId}
              name={product.productName}
              brand={product.productBrand}
              price={product.productPrice}
              rating={product.productRating}
              image={product.productImage}
              
              />
            )
          })
        }
        </DisplayFeatured>
     </FeaturedBox>

    );
  }
